mapper.select:
SELECT cp.*,u.level_id AS user_level_id,u.name AS user_name,u.photo AS user_photo,u.sex AS sex, (SELECT COUNT(*) FROM community_post_love cpl WHERE cpl.post_id = cp.id) AS love_count, (SELECT cpl.id FROM community_post_love cpl WHERE cpl.post_id = cp.id AND cpl.user_id = #{userId}) AS love_id, (SELECT COUNT(*) FROM community_post_collect cpc WHERE cpc.post_id = cp.id) AS collect_count, (SELECT cpc.id FROM community_post_collect cpc WHERE cpc.post_id = cp.id AND cpc.user_id = #{userId}) AS collect_id, (SELECT COUNT(*) FROM community_post_comment cpc WHERE cpc.post_id = cp.id) AS comment_count FROM community_post cp JOIN `user` u ON cp.user_id = u.id WHERE 1 = 1
AND cp.circle_id = #{circleId}
ORDER BY cp.create_time DESC LIMIT #{page.pageNo},#{page.pageSize}
