mapper.update:
update course_classes
user_id = #{userId}, name = #{name},
where id = #{id}

mapper.select:
select * from
course_classes cc where cc.course_id =
#{courseId}

SELECT * FROM course_classes cc
WHERE cc.name = #{0} AND cc.institution_id = #{2} AND cc.course_id =
#{1}

SELECT cc.id AS id,null AS institution_name,cc.cover AS cover,cc.user_id AS user_id,u.name AS user_name,cc.name AS `name`,cc.start_time AS start_time,cc.end_time AS end_time, cc.start_date AS start_date,cc.end_date AS end_date,cc.plan_count AS plan_count, cc.student_count AS student_count,cc.price AS price FROM course_classes cc JOIN `user` u ON cc.user_id = u.id WHERE cc.institution_id = #{institutionId}
AND cc.start_date <= DATE(#{date}) AND cc.end_date >= DATE(#{date})
LIMIT #{page.pageNo},#{page.pageSize}

SELECT cc.id AS id,null AS institution_name,cc.cover AS cover,cc.user_id AS user_id,u.name AS user_name,cc.name AS `name`,cc.start_time AS start_time,cc.end_time AS end_time, cc.start_date AS start_date,cc.end_date AS end_date,cc.plan_count AS plan_count, cc.student_count AS student_count,cc.price AS price FROM course_classes cc JOIN `user` u ON cc.user_id = u.id WHERE cc.id IN
#{classId}
ORDER BY cc.start_date DESC
