mapper.delete:
DELETE FROM
course_classes_sign where user_id = #{0}
AND classes_id = #{1}

mapper.select:
SELECT
COUNT(id) FROM course_classes_sign ccs WHERE ccs.user_id = #{0} AND
ccs.classes_id = #{1}

SELECT u.id AS id,u.name AS `name`,u.phone AS phone,u.photo AS photo,u.sex AS sex FROM `user` u WHERE u.id NOT IN (SELECT ccs.user_id FROM course_classes_sign ccs WHERE ccs.classes_id = #{classId}) AND u.id IN (select cco.user_id from course_classes_order cco where cco.class_id = #{classId})
AND (u.name LIKE '%${key}%' OR u.phone= #{key})
LIMIT #{page.pageNo},#{page.pageSize}
