mapper.update:
update institution u
u.name = #{name}, u.summary = #{summary}, u.address = #{address}, u.tel = #{tel}, u.parent_id = #{parentId}, u.region_id = #{regionId}, u.course_type = #{courseType}, u.logo = #{logo}, u.teacher_count = #{teacherCount} u.sign = #{sign}
where u.id = #{id}

mapper.select:
SELECT ii.* FROM
institution ii where ii.name like '%${name}%' limit
#{page.pageNo},#{page.pageSize}

mapper.update:
UPDATE
institution_user SET current = 1 WHERE user_id = #{userId} AND
institution_id = #{institutionId}

mapper.select:
SELECT CONCAT((case when i.region_id <> 0 THEN (SELECT rp.name FROM region r left JOIN region rp ON r.parent_id = rp.id WHERE r.id = i.region_id) ELSE '' END),(CASE when i.region_id <> 0 then (SELECT r.name FROM region r WHERE r.id = i.region_id) else '' END),' ', i.address) AS address,i.*,(SELECT COUNT(*) FROM institution_propa ip WHERE ip.institution_id = i.id) AS propa_count, (SELECT COUNT(*) FROM institution_comment WHERE institution_id = i.id) AS comment_count,(SELECT ABS(parent_id - #{regionId}) FROM region WHERE id = i.region_id) AS sort1 FROM institution i
where (i.tel = #{key} or i.name like '%${key}%')
ORDER BY sort1 limit #{page.pageNo},#{page.pageSize}

select i.*,(SELECT ic.name from institution ic WHERE ic.id =
i.parent_id) AS father_institution from institution i where i.id =
#{0}

SELECT ct.name FROM course c JOIN course_type ct ON
c.course_type_id = ct.id
WHERE c.institution_id = #{0}

SELECT c.name FROM course c JOIN course_classes cc ON c.id
= cc.course_id
WHERE cc.institution_id = #{institutionId}

SELECT u.* FROM `user` u WHERE u.type = 2 AND u.id NOT IN (SELECT uu.id FROM institution_user iu JOIN `user` uu ON iu.user_id = uu.id WHERE uu.type = 2 AND iu.institution_id = #{institutionId})
AND (u.name LIKE '%${key}%' OR u.phone = #{key})
LIMIT #{page.pageNo},#{page.pageSize}

mapper.delete:
DELETE FROM institution_user WHERE institution_id = #{institutionId}
AND user_id IN

	#{teacherId}

mapper.select:
SELECT u.* FROM `user` u WHERE u.type = 3 AND u.id NOT IN (SELECT uu.id FROM institution_user iu JOIN `user` uu ON iu.user_id = uu.id WHERE uu.type = 3 AND iu.institution_id = #{institutionId})
AND (u.name LIKE '%${key}%' OR u.phone = #{key})
LIMIT #{page.pageNo},#{page.pageSize}

SELECT i.*,(SELECT COUNT(*) FROM institution_propa ip WHERE ip.institution_id = i.id) AS propa_count, (SELECT COUNT(*) FROM institution_comment WHERE institution_id = i.id) AS comment_count FROM institution i JOIN institution_user iu ON i.id = iu.institution_id WHERE iu.user_id = #{userId}
AND i.name LIKE '%${key}%'
GROUP BY i.id LIMIT #{page.pageNo},#{page.pageSize}

SELECT i.*,(SELECT COUNT(*) FROM institution_propa ip WHERE ip.institution_id = i.id) AS propa_count, (SELECT COUNT(*) FROM institution_comment WHERE institution_id = i.id) AS comment_count FROM institution i WHERE i.id NOT IN (SELECT DISTINCT iu.institution_id FROM institution_user iu WHERE iu.user_id = #{userId})
AND i.name LIKE '%${key}%'
GROUP BY i.id LIMIT #{page.pageNo},#{page.pageSize}

