mapper.select:
select * from user u
where
u.phone = #{0} and u.password = #{1}

select * from user u
where
u.id = #{0}

mapper.update:
update user u

    u.name = #{name},
    u.level_id = #{levelId},
    u.type = #{type},
    u.address = #{address},
    u.sex = #{sex},
    u.birthday = #{birthday},
    u.region_id = #{regionId}
    u.real_name = #{realName}
    u.interest = #{interest}
    u.short_introduction = #{shortIntroduction}
    u.sign = #{sign}

where u.id = #{id}

