mapper.select:
SELECT * FROM user u JOIN institution_user iu ON u.id = iu.user_id WHERE iu.institution_id = #{institutionId} AND u.type = 2
AND (u.phone = #{key} OR u.name LIKE '%${key}%')
LIMIT #{page.pageNo},#{page.pageSize}

SELECT * FROM user u JOIN institution_user iu ON u.id = iu.user_id WHERE iu.institution_id = #{institutionId} AND u.type = 3
AND (u.phone = #{key} OR u.name LIKE '%${key}%')
LIMIT #{page.pageNo},#{page.pageSize}

SELECT * FROM user u WHERE u.type = 0 AND u.id <> #{userId}

	AND (u.phone = #{key} OR u.name LIKE '%${key}%')

 LIMIT
#{page.pageNo},#{page.pageSize}

SELECT * FROM user u WHERE
u.phone = #{key} OR u.name LIKE '%${key}%' LIMIT
#{page.pageNo},#{page.pageSize}

select * from user u
where
u.id = #{0}

SELECT * FROM user u WHERE 1 = 1
AND (u.phone = #{key} or u.name like '%${key}%')
AND u.id not in #{id}
LIMIT #{page.pageNo},#{page.pageSize}

SELECT * FROM user u WHERE 1 = 1
AND (u.phone = #{key} or u.name like '%${key}%')
AND u.id in #{id}

SELECT * FROM `user` u WHERE u.type <> 0

	AND (u.phone = #{key} OR u.name LIKE '%${key}%')

 LIMIT #{page.pageNo},#{page.pageSize}
